mapper.select:
/* */
SELECT bm.sicode , bm.kmmark , bm.m_code , bm.m_group , bm.m_autoid , bm.m_regist , DATE_FORMAT(STR_TO_DATE(bm.m_enday, '%Y-%m-%d'), '%Y.%m.%d') AS m_enday , IF(bm.m_reday = STR_TO_DATE('21000101','%Y%m%d'), '', DATE_FORMAT(STR_TO_DATE(bm.m_reday, '%Y-%m-%d'), '%Y.%m.%d')) AS m_reday , bm.m_name , bm.성별 AS sex_gb , bm.나이 , bm.m_jumin1 , CONCAT(SUBSTRING(bm.m_jumin1,1,2), '-',SUBSTRING(bm.m_jumin1,3,2),'-',SUBSTRING(bm.m_jumin1,5,2)) AS jumin , bm.m_grade , bm.등급 AS grade_nm , CASE WHEN bm.식사종류 = '1' THEN '일반식' WHEN bm.식사종류 = '2' THEN '죽식' WHEN bm.식사종류 = '3' THEN '유동식(미음)' ELSE '' END 식사종류명 , bm.m_level , bm.수급 , bm.거주현황 AS m_type , bm.m_roomno , bm.m_step , bm.m_roomname , bm.단기보호 AS pr_cd , CASE WHEN bm.단기보호 = '1' THEN '주간' WHEN bm.단기보호 = '2' THEN '주단' WHEN bm.단기보호 = '3' THEN '단기' END AS pr_nm , CASE WHEN IFNULL(kr.미입소,'') != '' && bm.단기보호 = '1' THEN '1' WHEN IFNULL(kr.미입소,'') != '' && bm.단기보호 IN ('2', '3') THEN '2' WHEN bm.단기보호 IN ('2', '3') THEN '3' ELSE '0' END pr_css , bm.수급생일 , IFNULL(TRIM(kr.미입소),'') AS move_cont FROM base_mate bm LEFT JOIN (SELECT * FROM kmrecord WHERE sicode = #{sicode} AND k_nalja = STR_TO_DATE(#{s_date},'%Y%m%d')) kr ON bm.sicode = kr.sicode AND bm.m_code = kr.k_code WHERE bm.sicode = #{sicode} AND STR_TO_DATE(#{s_date},'%Y%m%d') BETWEEN bm.m_enday AND bm.m_reday AND SUBSTRING(CONCAT(bm.일요일,bm.이용일),DATE_FORMAT(STR_TO_DATE(#{s_date},'%Y%m%d'),'%w')+1,1) = 1
AND bm.m_group = #{m_group}
ORDER BY bm.m_name

    /*  */
		SELECT COUNT(*) AS cnt 
		FROM base_bath 
		WHERE sicode = #{sicode}
			AND ba_nalja = #{date}

    /*  */
		SELECT COUNT(*) AS cnt 
		FROM base_bath 
		WHERE sicode = #{sicode}
			AND ba_nalja = #{date}
			AND ba_code = #{m_code}

mapper.insert:
    /*  */
    	INSERT INTO kmrecord
		(
			  sicode      
			, k_nalja
			, k_code
			, k_name
			, 아침양
			, 아침종류
			, 점심양
			, 점심종류
			, 저녁양
			, 저녁종류
			, 오전간식
			, 오후간식
			, 아침시
			, 아침분
			, 점심시
			, 점심분
			, 저녁시
			, 저녁분
			, 요양일지
			, 일지담당
			, 일지코드
			, 구강담당
			, 구강코드
			, 구강상태
			, 구강내용
		)
		VALUES
		(
			  #{sicode}
			, #{k_nalja}			
			, #{m_code}
			, #{m_name}
			, #{아침양}
			, #{아침종류}
			, #{점심양}
			, #{점심종류}
			, #{저녁양}
			, #{저녁종류}
			, #{오전간식}
			, #{오후간식}
			, #{아침시}
			, #{아침분}
			, #{점심시}
			, #{점심분}
			, #{저녁시}
			, #{저녁분}
			, #{요양일지}
			, #{일지담당}
			, #{일지코드}
			, #{구강담당}
			, #{구강코드}
			, #{구강상태}
			, #{구강내용}
		)

mapper.update:
    /*  */
    	UPDATE kmrecord
		  SET 아침양  = #{아침양}
			, 아침종류 = #{아침종류}
			, 점심양  = #{점심양}
			, 점심종류 = #{점심종류}
			, 저녁양  = #{저녁양}
			, 저녁종류 = #{저녁종류}
			, 오전간식 = #{오전간식}
			, 오후간식 = #{오후간식}
			, 아침시  = #{아침시}
			, 아침분  = #{아침분}
			, 점심시  = #{점심시}
			, 점심분  = #{점심분}
			, 저녁시  = #{저녁시}
			, 저녁분  = #{저녁분}
			, 요양일지 = #{요양일지}
			, 일지담당 = #{일지담당}
			, 일지코드 = #{일지코드}
			, 구강담당 = #{구강담당}
			, 구강코드 = #{구강코드}
			, 구강상태 = #{구강상태}
			, 구강내용 = #{구강내용}
		WHERE sicode = #{sicode}
		AND k_number = #{k_number}

mapper.insert:
    /*  */
    	INSERT INTO kmdaily25
		(
			sicode
			, kmdaily
			, kmcode
			, kmname
			, km바코드
			, km직원명
			, km직원코드
			, km제공시간
			, km저장일시
			, km평가
			, org_kmnumber
		)
		VALUES
		(
			#{sicode}
			, #{kmdaily}
			, #{kmcode}
			, #{kmname}
			, #{km바코드}
			, #{km직원명}
			, #{km직원코드}
			, IFNULL(#{km제공시간},'')
			, NOW()
			, IFNULL(#{km평가},'')
			, #{org_kmnumber}
		)

mapper.delete:
/*  */
	DELETE FROM kmdaily25
	WHERE sicode = #{sicode}
	AND	kmnumber = #{kmnumber}
