	DELETE FROM km_homedrug
	 WHERE sicode = #{sicode}
      AND 점검번호 = #{점검번호}
      AND 점검일 = #{점검일}

mapper.select:
    /* 2-04 CHK */
	SELECT sicode
	     , 점검일
	  FROM km_homedrug
	 WHERE sicode = #{sicode}
	   AND 점검일 = #{hd_date}
