SELECT COMPANY_CD , A.RES_ID , A.NAME , FLOOR( (CAST(REPLACE(CURRENT_DATE,'-','') AS UNSIGNED) - CAST(REPLACE(A.BIRTHDAY,'-','') AS UNSIGNED)) / 10000 ) AS AGE , CONCAT(A.NAME,'(',FLOOR( (CAST(REPLACE(CURRENT_DATE,'-','') AS UNSIGNED) - CAST(REPLACE(A.BIRTHDAY,'-','') AS UNSIGNED)) / 10000 ),')') AS NAME_AGE , DATE_FORMAT(A.ENTER_DT,'%Y.%m.%d') AS ENTER_DT , A.AREA , A.LOCATION , CONCAT((SELECT B.ARE_NM FROM T_NU_AREA B WHERE B.COMPANY_CD = A.COMPANY_CD AND B.ARE_CD = A.AREA), '/', (SELECT B.LOC_NM FROM T_NU_LOCATION B WHERE B.COMPANY_CD = A.COMPANY_CD AND B.LOC_CD = A.LOCATION)) AS ROOM , #{DATE} AS PRE_DT , #{DATE} AS PRE_DT2 , REF_SYS_RES_ID , (SELECT CC.REF_SYS_COMP_CD FROM T_CM_COMPANY CC WHERE CC.COMPANY_CD = A.COMPANY_CD) REF_SYS_COMP_CD FROM T_NU_RESIDENT A WHERE 1=1
AND COMPANY_CD = #{COMPANY_CD}
AND RES_ID = #{RES_ID}

SELECT NT.COMPANY_CD , NT.TEM_SEQ , NT.RES_ID , NT.TEM_DT , NT.TEM_TIME , CONCAT(NT.TEM_DT, SUBSTRING(NT.TEM_TIME, 1, 2), SUBSTRING(NT.TEM_TIME, 3, 2), '00') AS TEM_TIME2 , NT.TEMPERATURE , NT.IR_TEMP , NT.IR_TEMP_TIME , NT.MANAGER_ID , NT.REMARK , NT.REG_PGM_ID , NT.REG_USER , NT.REG_DT , NT.REG_DT_CURR , NT.REG_DT_LOC , NT.REG_IP , NT.MOD_PGM_ID , NT.MOD_USER , NT.MOD_DT , NT.MOD_DT_CURR , NT.MOD_DT_LOC , NT.MOD_IP , 'ds_temperature' AS INDS , 'N' AS EDIT , 'N' AS DEL , NR.REF_SYS_RES_ID , CC.REF_SYS_COMP_CD FROM T_NU_TEMPERATURE NT ,T_NU_RESIDENT NR ,T_CM_COMPANY CC WHERE NT.COMPANY_CD = NR.COMPANY_CD AND NT.RES_ID = NR.RES_ID AND NT.COMPANY_CD = CC.COMPANY_CD
AND NT.COMPANY_CD = #{COMPANY_CD}
AND NT.RES_ID = #{RES_ID}
AND NT.TEM_DT = #{DATE}
ORDER BY NT.TEM_TIME ASC


            SELECT CASE WHEN COUNT(*)>0 THEN 'Y'
