/* NUB205CT_NS_SCT_HEALTH_PRE_I */ INSERT INTO sct_health ( sicode , n_nalja , n_code , n_name , 담당자 , 담당코드 , s혈압 , s혈압시간 , s맥박 , s맥박시간 , ir_혈압 , 혈압시간 , ir_맥박 , 맥박시간 , ir_time , ir_type , ir_comp , ir_status ) VALUES ( (SELECT CC.REF_SYS_COMP_CD FROM T_CM_COMPANY CC WHERE CC.COMPANY_CD = #{COMPANY_CD})
, #{PRE_DT}
, #{SUG_DT}
, #{TEM_DT}
, IFNULL((SELECT NR.REF_SYS_RES_ID FROM T_NU_RESIDENT NR WHERE NR.COMPANY_CD = #{COMPANY_CD} AND NR.RES_ID = #{RES_ID}), '') , IFNULL((SELECT NR.NAME FROM T_NU_RESIDENT NR WHERE NR.COMPANY_CD = #{COMPANY_CD} AND NR.RES_ID = #{RES_ID}), DEFAULT(n_name)) , IFNULL((SELECT CU.USER_NM_KOR FROM T_CM_USER CU INNER JOIN T_CM_COMPANY CC ON CC.COMPANY_CD = CU.COMPANY_CD INNER JOIN sct_emp SE ON CC.REF_SYS_COMP_CD = SE.sicode AND CU.REF_USER_ID = SE.c_staffid WHERE CU.COMPANY_CD = #{COMPANY_CD} AND CU.USER_ID = #{MANAGER_ID}), DEFAULT(담당자)) , IFNULL((SELECT CU.REF_USER_ID FROM T_CM_USER CU INNER JOIN T_CM_COMPANY CC ON CC.COMPANY_CD = CU.COMPANY_CD INNER JOIN sct_emp SE ON CC.REF_SYS_COMP_CD = SE.sicode AND CU.REF_USER_ID = SE.c_staffid WHERE CU.COMPANY_CD = #{COMPANY_CD} AND CU.USER_ID = #{MANAGER_ID}), DEFAULT(담당코드)) , IFNULL(CONCAT(LPAD(#{PRE_H},3,' '),'/',LPAD(#{PRE_L},3,' ')), DEFAULT(s혈압)) , IFNULL(#{PRE_TIME2}, DEFAULT(s혈압시간)) , IFNULL(#{PULSE}, DEFAULT(s맥박)) , IFNULL(#{PRE_TIME2}, DEFAULT(s맥박시간)) , IFNULL(CONCAT(LPAD(#{IR_PRE_H},3,' '),'/',LPAD(#{IR_PRE_L},3,' ')), DEFAULT(ir_혈압)) , IFNULL(#{IR_PRE_TIME}, DEFAULT(혈압시간)) , IFNULL(#{IR_PULSE}, DEFAULT(ir_맥박)) , IFNULL(#{IR_PRE_TIME}, DEFAULT(맥박시간)) , NOW() , 'I' , 'S' , '0' )

